styles.styleSheet:
/* Specific style overrides go outside the root definition */

    /****** Form area *****/

    app-form-engine {
      background-color: #ececec;
    }
    /* Form field type outline */
    app-form-engine mat-form-field .mat-form-field-outline {
      background-color: #fff;
    }

    /* Radio button */
    app-form-engine mat-radio-button .mat-radio-outer-circle {
      background-color: #fff;
    }

    app-form-engine mat-checkbox .mat-checkbox-inner-container {
      background-color: #fff;
    }

    app-form-engine a {
      color: var(--primary-color);
    }

    /****** Content area ******/

    /* Custom list item bullet points (https://www.w3.org/Style/Examples/007/color-bullets.en.html) */
    app-content-article ul {
      list-style: none;
      padding: 0 0 0 1em;
    }
    app-content-article li::before {
      content: "•";
      color: var(--primary-color);
      display: inline-block; width: 1em;
      margin-left: -1em;
    }
    app-content-article li {
      margin-bottom: 16px;
    }

    /* Coloured headings, links */
    app-content-article a,
    app-content-article h3 {
      color: var(--primary-color);
    }

    /****** Typography ******/
    h1.mat-h1 {}
    h2.mat-h2 {}
    h3.mat-h4 {}
    h4.mat-h4 {}
    .mat-body-1 p {}
    .mat-body-2 p {}
